  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };
  const toggledarkmode = () => {
    if(color === "Dark-Mode"){
      changemode()
    }
  };

  return (
    <div className="sidebar-container">
      {/* Toggle Button */}
      <button className="toggle-button" onClick={toggleSidebar}>
        {isOpen ? '→' : '←'}
      </button>

      {/* Sidebar */}
      <div className={`sidebar ${isOpen ? 'open' : 'closed'}`}>
