import React, { useState, useEffect } from 'react';

function Pokemon() {
  const [pokemonList, setPokemonList] = useState([]);
  const [selectedPokemon, setSelectedPokemon] = useState(null);

  useEffect(() => {
    fetch('https://pokeapi.co/api/v2/pokemon?limit=20')
      .then((response) => response.json())
      .then((data) => {
        const fetchPokemonDetails = data.results.map((pokemon) =>
          fetch(pokemon.url).then((res) => res.json())
        );
        
        Promise.all(fetchPokemonDetails).then((detailedData) => {
          setPokemonList(detailedData);
        });
      });
  }, []);

  const handlePokemonClick = (pokemon) => {
    setSelectedPokemon(pokemon);
  };

  return (
    <div className="p-4 bg-white rounded-lg shadow-lg max-w-md">
      <h1 className="text-2xl font-bold mb-4 text-center">Pok√©dex</h1>
      <ul className="space-y-3">
        {pokemonList.map((pokemon) => (
          <li
            key={pokemon.id} // Adding a unique key here
            className="p-4 bg-gray-200 rounded-lg cursor-pointer"
            onClick={() => handlePokemonClick(pokemon)}
          >
            <img src={pokemon.sprites.front_default} alt={pokemon.name} className="w-16 h-16 mx-auto" />
            <h2 className="text-center font-semibold">{pokemon.name}</h2>
            <p className="text-center text-gray-700">
              {pokemon.types.map((typeInfo) => typeInfo.type.name).join(", ")}
            </p>
          </li>
        ))}
      </ul>

      {selectedPokemon && (
        <div className="mt-6 p-4 bg-indigo-100 rounded-lg">
          <h2 className="text-lg font-bold">{selectedPokemon.name}</h2>
          <p>ID: {selectedPokemon.id}</p>
          <p>Height: {selectedPokemon.height}</p>
          <p>Weight: {selectedPokemon.weight}</p>
          <p>
            Types: {selectedPokemon.types.map((type) => type.type.name).join(", ")}
          </p>
          <button onClick={() => setSelectedPokemon(null)} className="mt-4 text-indigo-600">
            Close
          </button>
        </div>
      )}
    </div>
  );
}

export default Pokemon;
