import React, { useState, useEffect } from 'react';

function Pokemon() {
  const [pokemonList, setPokemonList] = useState([]);
  const [selectedPokemon, setSelectedPokemon] = useState(null);

  useEffect(() => {
    fetch('https://pokeapi.co/api/v2/pokemon?limit=50')
      .then((response) => response.json())
      .then((data) => {
        const fetchPokemonDetails = data.results.map((pokemon) =>
          fetch(pokemon.url).then((res) => res.json())
        );
        
        Promise.all(fetchPokemonDetails).then((detailedData) => {
          setPokemonList(detailedData);
        });
      });
  }, []);

  const handlePokemonClick = (pokemon) => {
    setSelectedPokemon(pokemon);
  };
  const getClassName = (type) =>{
    if(type === 'fire'){
      return 'badge rounded-pill text-bg-danger';
    }
    if(type === 'grass'){
      return 'badge rounded-pill text-bg-success';
    }
    if(type === 'water'){
      return 'badge rounded-pill text-bg-primary';
    }
    if(type === 'electric'){
      return 'badge rounded-pill text-bg-warning';
    }
    if(type === 'bug'){
      return 'badge rounded-pill badge-bug';
    }
    if(type === 'normal'){
      return 'badge rounded-pill badge-normal';
    }
    if(type === 'poison'){
      return 'badge rounded-pill badge-poison';
    }
    if(type === 'poison'){
      return 'badge rounded-pill badge-poison';
    }
    
  }

  return (
      <div class="list-group">
        <ul class="list-group list-group-flush">
          {pokemonList.map((pokemon)=>(
            <li class="list-group-item"
                key={pokemon.id}
                className="p-4 bg-gray-200 rounded-lg cursor-pointer"
                onClick={() => handlePokemonClick(pokemon)}>
                <a href="#" class="list-group-item list-group-item-action">
                <img href="#" class="list-group-item list-group-item-action list-group-item-primary" src={pokemon.sprites.front_default} alt={pokemon.name} className="w-16 h-16 mx-auto" />
                <span className={getClassName(pokemon.types[0].type.name)}>{pokemon.name} {pokemon.types[0].type.name}</span>
                /a></a>
            </li>
          ))}
          
          
        </ul>
      </div>

  );
}

export default Pokemon;
